class Blusa:
    def __init__(self):
        self.__tamanho: str 

    def getTam(self):
        return self.__tamanho

    def setTam(self, numeracao):
        if numeracao != "PP" or "P" or "M" or "G" or "GG" or "XG":
            print("numeração não encontrada, tamanhos disponíveis: PP, P, M, G, GG e XG")
        else:
            self.__tamanho = numeracao 


blusa = Blusa()
blusa.setTam("M")
print(blusa)
